            #核心模块指令，智力默认设置为二进制流，也就是当文件类型未定义时使用这种方式
            default_type  application/octet-stream;
            #开启高效文件传输模式
            sendfile        on;
            keepalive_timeout  65;
            ########Nginx的server虚拟主机配置
            server {
