  return request({
    url: 'api/menus/build',
    method: 'get'
  })
}
//post 请求保存数据
export function add(data) {
  return request({
    url: 'api/menus',
    method: 'post',
    data
  })
}
//delete 请求删除数据
export function del(id) {
  return request({
    url: 'api/menus/' + id,
    method: 'delete'
  })
}
